define([ 'jquery', 'Modernizr', 'sassToJs' ], function ( $, Modernizr, sassToJs ) {

    function normilizeBreakpoint (breakpoint) {

        let keyWords = [ 'min-width', 'max-width' ],
            regExp = /^(\d+..)(\d+..)$/g,
            result = '',
            matched = false;

        for( let key in keyWords) {

            let re = new RegExp(keyWords[key]);

            if (re.test(breakpoint) && !matched) {

                result = breakpoint.replace(re, keyWords[key] + ': ');
                matched = true;

            }

        }

        if ( regExp.test(breakpoint) && !matched) {

            result = breakpoint.replace(regExp,
                                `(min-width: $1) and (max-width: $2)`);

        }

        return result;

    }

    function removeBreakpointClasses (root, keys) {

        for(let i = 0; i < keys.length; i++) {

            root.classList.remove(keys[i]);

        }

    }

    return function () {

        let breakpointEl = $('.breakpoints'),
            breakpointsData = sassToJs(breakpointEl[0]),
            breakpoints = breakpointsData.breakpoints,
            root = document.getElementsByTagName( 'html' )[0],
            keys = Object.keys(breakpoints);

        removeBreakpointClasses(root, keys);
        keys.forEach(function (key) {

            if (Modernizr.mq(normilizeBreakpoint(breakpoints[key]))) {

                root.classList.add(key);

            }

        });

    };

});

require( [ 'jquery', 'resizeFunc', 'contact/custom/formChecker' ], function ($, resizeF, formChecker) {

    resizeF(3);

    $('.contact_hire_form_submit').on('click', function (e) {
            let $form = $('.contact_hire_form'),
                formData = $form.serialize(),
                $preload = $('.preload'),
                $email_result_message = $('.email_result_message'),
                $email_result = $('.email_result'),
                $email_result_cover = $('.email_result_cover'),
                $email_result_button = $('.email_result_button'),
                formCheck = new formChecker($form);

            e.preventDefault();

            if (formCheck.ifVerified()) {

                $preload.delay(200).show();
                $.ajax({
                    url      : "/contact/hire",
                    method   : "POST",
                    data     : formData,
                    dataType : "text"
                }).then(function (msg){

                    showHideEmailBlock(msg);

                },
                function (error) {

                    showHideEmailBlock(error);

                });

            }

            function showHideEmailBlock (msg) {

                $preload.hide(100,function() {
                    $email_result_cover.show();
                    $email_result_message.text(msg);
                    $email_result_button.one('click', function() {
                        $form.find('.contact_hire_form_input, textarea').val('');
                        $email_result_cover.hide();
                    });
                });

            }

        });

});

define([ 'jquery', 'jqueryeasing' ], function ($, $e) {

    class Portfolio {
        constructor(catLinks) {

            this.catLinks = catLinks;
            this.catLinksVisible = this.catLinks.filter(':visible');
            this.arrow = this.catLinks.filter('.arrow');
            this.parent = catLinks.parent();

        }

        clear() {

            this.catLinks.remove();

        }

        hideAll() {

            this.catLinks.css('display', 'none');

        }

        goDown(el) {

            let href = el.find('a').attr('href');
            this.hideAll();
            this.arrow.show();
            this.catLinks.find('a[href*="' + href + '/"]').parent().fadeIn(1000, 'easeInOutExpo');

        }

        goUp() {

            this.hideAll();
            this.catLinksVisible.show();

        }

        start() {
            let that = this;

            this.arrow.on('click', function (event) {

                event.preventDefault();
                that.goUp();

            });

            this.catLinksVisible.on('click', function (event) {

                event.preventDefault();
                that.goDown($(this));

            });

        }
    }

    return Portfolio;

});

define(['jquery'], function ($) {

    function showhideText(els) {

        let timeout = 3,
            l = els.length,
            i = 0;

        els.hide();

        function go () {

            els.eq(i % l).fadeIn(1000, function () {

                els.eq(i % l).delay(timeout * 1000).fadeOut(1000, go);
                i++;

            });

        }

        go();

    }

    return showhideText;

});

define([ 'jquery' ], function ( $ ) {

    function checkEmail (value) {

        let regExp = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

        return value.match(regExp) ? true : false;

    }

    class formChecker {

        constructor(form) {

            this.$form = form;
            this.$inputs = this.$form.find('.contact_hire_form_input');
            this.$email = this.$form.find('#client_email');
            this.$fields = this.$form.find('.contact_hire_form_input, textarea');
            this.$textArea = this.$form.find('textarea');
            this.$errors = this.$form.find('.error_message');

        }

        ifVerified () {

            let isOk = true;

            this.$errors.hide();
            this.$fields.each(function () {

                let current = $(this);

                if (current.val().trim().length === 0) {

                    current.next('.error_message').fadeIn('slow').delay(3000).fadeOut('slow');
                    isOk = false;

                }

                if (current.is('#client_email') && current.val().trim().length > 0) {

                    if (!checkEmail(current.val().trim())) {

                        current.next('.error_message').fadeIn('slow').delay(3000).fadeOut('slow');
                        isOk = false;

                    }

                }

            });

            return isOk;

        }

    }

    return formChecker;

});
